import React, { useState } from "react";
import searchwhite from '../assets/images/searchwhite.png'
import iconclose from '../assets/images/iconclose.png'
// import "./SearchBar.css";
// import SearchIcon from "@material-ui/icons/Search";
// import CloseIcon from "@material-ui/icons/Close";

function SearchBar({ placeholder, data }) {
  const [filteredData, setFilteredData] = useState([]);
  const [wordEntered, setWordEntered] = useState("");

  const handleFilter = (event) => {
    const searchWord = event.target.value;
    setWordEntered(searchWord);
    const newFilter = data.filter((value) => {
      return value.title.toLowerCase().includes(searchWord.toLowerCase());
    });

    if (searchWord === "") {
      setFilteredData([]);
    } else {
      setFilteredData(newFilter);
    }
  };

  const clearInput = () => {
    setFilteredData([]);
    setWordEntered("");
  };

  const pushLink=(slug)=>{
    history.pushState('searchbook/'+slug)
  }

  return (
    <div className="search">
      <div className="searchInputs">
        <input
          type="text"
          placeholder={placeholder}
          value={wordEntered}
          onChange={handleFilter}
        />
        <div className="searchIcon">
          {filteredData.length === 0 ? (
            // <i class='bx bx-search-alt-2'></i>
            <img src={searchwhite} alt=""/>
          ) : (
            <img src={iconclose} d="clearBtn" onClick={clearInput} alt=""/>
            // <i class='bx bx-x' id="clearBtn" onClick={clearInput}></i>
          )}
        </div>
        
      </div>
      {filteredData.length != 0 && (
        <div className="dataResult">
          {filteredData.slice(0, 9).map((value, key) => {
            return (
              <a className="dataItem" href={'searchbook/'+value.slug} target="_blank">
                <p>{value.title} </p>
              </a>
            );
          })}
        </div>
      )}
    </div>
  );
}

export default SearchBar;
