import React, { useState, useEffect } from 'react'
import {useParams, useLocation} from 'react-router-dom';
// import PropTypes from 'prop-types'

import { withRouter } from 'react-router'

import Button from './Button'
import bookData from '../assets/data-loaded/books'

const BorrowBook = () => {
//     const navigate = useNavigate();

//   const navigateToBorrow = () => {
//     navigate('/borrow');
//   };

    //const product = bookData.getBookBySlug(props.match.params.slug)

    // const image = useLocation().state.image;
    // const title=useLocation().state.title;
    // const author=useLocation().state.author;
    // const description=useLocation().state.description;
    // console.log(image);
    // console.log(title);
    // console.log(author);
    // console.log(description);
    
    // const [previewImg, setPreviewImg] = useState(image)

    // const [descriptionExpand, setDescriptionExpand] = useState(false)

    useEffect(() => {
        // setPreviewImg(image)
        const bgcolor = "#DDFFF9"
        document.body.style.background = bgcolor;
    })
    const stateParamValue=useLocation().state;
    const product=bookData.getBookBySlug(stateParamValue);
    console.log(product);

    return (
        <div className="borrowbook">
            <div className="borrowbook__images">
                <div className="borrowbook__images__main">
                    <img src={product.image} alt="" />
                </div>
                
            </div>
            <div className="borrowbook__info">
                <h3 className="borrowbook__info__title">{product.title}</h3>
                <h3 className="borrowbook__info__author">
                    {product.author}
                </h3>
                <div className="borrowbook__info__item">
                    <div className="borrowbook__info__item__content"> {product.description}</div>
                </div>
            </div>
        </div>
    )
}

