import React from 'react'
import { Link } from 'react-router-dom'

import Helmet from '../components/Helmet'
import Section, { SectionTitle, SectionBody } from '../components/Section'
import PolicyCard from '../components/PolicyCard'
import Grid from '../components/Grid'
import ProductCard from '../components/ProductCard'

// import heroSliderData from '../assets/fake-data/hero-slider'
import policy from '../assets/data-loaded/policy'
import productData from '../assets/data-loaded/products'

import SimpleImageSliderHome from '../components/SimpleImageSliderHome'


const Home = () => {
    return (
        <Helmet title="Home">
            {/* slide image */}
            <Section>
                <SectionBody>
                <SimpleImageSliderHome/>
                </SectionBody>
            </Section>
            {/* end slide image */}

            <Section>
                <SectionBody>
                    <div className='introcontainer'>
                    <Grid
                        col={3}
                        mdCol={2}
                        smCol={1}
                        gap={20}
                    >
                        {
                            policy.map((item, index) =>
                                <PolicyCard
                                    name={item.name}
                                    description={item.description}
                                    icon={item.icon}
                                />
                            )
                        }
                    </Grid>
                    </div>
                </SectionBody>
            </Section>
            {/* end intro to features */}


            {/* new arrival section */}
            <Section>
                <SectionTitle>
                    New books
                </SectionTitle>
                <SectionBody>
                    <Grid
                        col={4}
                        mdCol={2}
                        smCol={1}
                        gap={20}
                    >
                        {
                            productData.getProducts(8).map((item, index) => (
                                <ProductCard
                                    key={index}
                                    img01={item.image01}
                                    img02={item.image02}
                                    name={item.title}
                                    price={Number(item.price)}
                                    slug={item.slug}
                                />
                            ))
                        }
                    </Grid>
                </SectionBody>
            </Section>
            {/* end new arrival section */}

        </Helmet>
    )
}

export default Home
