import React from 'react'
import styled from "styled-components";
const YourAppointment = () => {
    const product={
        title: "Sherlock Homes",
        author: 'Arthur Conan Doyle',
        image: require('../assets/images/booksImg/book1 new.jpg'),
        image02: require('../assets/images/booksImg/book1_2.jpg'),
        image03: require('../assets/images/booksImg/book1.jpg'),
        image04: require('../assets/images/booksImg/book1_2.jpg'),
        genreSlug: "Fiction",
        slug: "sherlock-homes",
        bookType: 'Ebook',
        description:'Sherlock Holmes is a fictional detective created by British author Arthur Conan Doyle. Referring to himself as a "consulting detective" in the stories, Holmes is known for his proficiency with observation, deduction, forensic science and logical reasoning that borders on the fantastic, which he employs when investigating cases for a wide variety of clients, including Scotland Yard.',
    }
    const Button = styled.button`
  color: palevioletred;
  font-size: 1em;
  margin: 1em;
  padding: 0.25em 1em;
  border: 2px solid palevioletred;
  border-radius: 3px;
`;
    return (
    // <div className="your-appointment">
    //     <div className="container">
    //         <div className="your-appointment__text">
    //             <p>
    //                 Your appointment
    //             </p>
    //         </div>
    //         <div className="your-appointment__noappointment">
    //             <p>
    //                 You don't have any appointment yet.
    //                 Go to Search book to search for printed book and borrow
    //             </p>   
    //         </div>
    //     </div>
    // </div>
    
    // <div className="yourappointment">
    //     {
          
    //         <div className="yourappointment__details">
    //           <h3 className="yourappointment__details__appointmentuser">Linda Brown (lindabrown)</h3>
    //           <div className="yourappointment__details__big-img">
    //             <img src={product.image} alt=""/>
    //           </div>

    //           <div className="yourappointment__details__box">

    //             <p className="yourappointment__details__box__productitle">{product.title}</p>
    //             <p className="yourappointment__details__box__productauthor">{product.author}</p>

    //             <button className="yourappointment__details__box__checkdone">Done</button>

    //           </div>
    //         </div>

    //     }
    //   </div>
    
    <Container>
      <NavBar>01/14/2022 - 2PM</NavBar>
    
      <Main>{product.title}</Main>
      <SideBar>
        <img src={product.image} alt=""/>
      </SideBar>
      <ContentBox>
        {product.author}
      </ContentBox>
      <Footer>
        <Button className="yourappointment__details__box__checkdone">Done</Button>
      </Footer>
  </Container>
  )
}

const Container = styled.div`
  display: grid;
  height: 100vh;
  grid-template-rows: 10px 10px 40px 8px;
  grid-template-areas:
    "nav nav"
    "sidebar main"
    "sidebar content"
    "footer footer";
  text-align: center;
  grid-gap: 0.25rem;
  transition: all 0.25s ease-in-out;
  @media (max-width: 550px) {
    grid-template-columns: 1fr;
    grid-template-rows: 0.4fr 0.4fr 2.2fr 1.2fr 1fr;
    grid-template-areas:
      "nav"
      "sidebar"
      "main"
      "content"
      "footer";
  }
  color: black;
`;
const NavBar = styled.nav`
  background: white;
  grid-area: nav;
  padding: 0.25rem;
`;
const Main = styled.main`
  background: white;
  color: black;
  grid-area: main;
  padding: 0.25rem;
`;
const SideBar = styled.div`
  background: white;
  grid-area: sidebar;
  padding: 0.25rem;
`;

const ContentBox = styled.div`
background: white;
  display: flex;
  gap: 0.25rem;
  padding: 0.25rem;
  align-items: center;
  grid-area: content;
  justify-content: center;
  @media (max-width: 550px) {
    flex-direction: column;
  }
`;
const Footer = styled.footer`
  background: white;
  grid-area: footer;
  padding: 0.25rem;
`;

export default YourAppointment
